mapper.update:
update  video
	set 
		VIP_FLAG = #{VIP_FLAG}
	where 
		VIDEO_ID = #{VIDEO_ID}

update  video
	set 
		BANNER_FLAG = #{BANNER_FLAG}
	where 
		VIDEO_ID = #{VIDEO_ID}

update  video
	set 
		HOME_FLAG = #{HOME_FLAG}	
	where 
		VIDEO_ID = #{VIDEO_ID}

update  video
	set 
		COMPETITIVE_FLAG = #{COMPETITIVE_FLAG}
	where 
		VIDEO_ID = #{VIDEO_ID}

update  video
	set 				
		RECOMMEND_FLAG = #{RECOMMEND_FLAG}
	where 
		VIDEO_ID = #{VIDEO_ID}

mapper.select:
select a.NAME_ONE, a.NAME_TWO, a.IMG_ONE, a.IMG_TWO, a.SUMMARY_ONE, a.SUMMARY_TWO, a.VIDEO_URL, a.VIDEO_URL_TWO, a.COLUMN_ID, a.VIDEO_CATEGORY1, a.VIDEO_CATEGORY2, a.DATA_TYPE, a.POWER, a.VIP_FLAG, a.BANNER_FLAG, a.HOME_FLAG, a.COMPETITIVE_FLAG, a.RECOMMEND_FLAG, a.ADD_TIME, a.UPDATE_TIME, a.VIDEO_ID, b.NAME_ONE as COLUMN_NAME_ONE, b.NAME_TWO as COLUMN_NAME_TWO from video a left join tab_column b on a.COLUMN_ID =b.COLUMN_ID
where a.COLUMN_ID = #{pd.column_id}
ORDER BY a.POWER,a.UPDATE_TIME DESC

select
		a.*
from 
		video a
where 1=1

	and a.COLUMN_ID = #{column_id} 


	and a.COLUMN_ID = #{COLUMN_ID} 


	and a.DATA_TYPE = #{DATA_TYPE} 

ORDER BY a.POWER,a.UPDATE_TIME DESC

select
		a.*,
		 RAND() as RANDSORT
from 
		video a
where 1=1


	and a.COLUMN_ID = #{COLUMN_ID} 


	and a.VIP_FLAG = #{VIP_FLAG} 


	and a.COMPETITIVE_FLAG = #{COMPETITIVE_FLAG} 


	and a.RECOMMEND_FLAG = #{RECOMMEND_FLAG} 


	and a.DATA_TYPE = #{DATA_TYPE} 

ORDER BY RANDSORT

select a.VIDEO_ID, a.NAME_ONE, a.NAME_TWO, a.IMG_ONE, a.IMG_TWO, a.SUMMARY_ONE, a.SUMMARY_TWO, a.VIDEO_URL, a.VIDEO_URL_TWO, a.COLUMN_ID, a.VIDEO_CATEGORY1, a.VIDEO_CATEGORY2, a.DATA_TYPE, a.VIP_FLAG, a.BANNER_FLAG, a.HOME_FLAG, a.COMPETITIVE_FLAG, a.RECOMMEND_FLAG, a.POWER, a.ADD_TIME, a.UPDATE_TIME, a.VIDEO_ID from video a where a.STATUS= -1
and a.COLUMN_ID = #{column_id}
and a.COLUMN_ID = #{COLUMN_ID}
and a.DATA_TYPE = #{DATA_TYPE}
ORDER BY a.POWER,a.UPDATE_TIME DESC

SELECT A.*
FROM video A
WHERE 1 = 1		

	and a.COLUMN_ID = #{COLUMN_ID} 


	and a.VIP_FLAG = #{VIP_FLAG} 


	and a.COMPETITIVE_FLAG = #{COMPETITIVE_FLAG} 


	and a.RECOMMEND_FLAG = #{RECOMMEND_FLAG} 


	and a.DATA_TYPE = #{DATA_TYPE} 

ORDER BY A.POWER,A.ADD_TIME DESC
LIMIT  #{PAGE_FROM} ,#{PAGE_SIZE}

SELECT COUNT(A.VIDEO_ID) AS VIDEO_CNT		       
FROM video A
WHERE 1 = 1


	and a.COLUMN_ID = #{COLUMN_ID} 


	and a.VIP_FLAG = #{VIP_FLAG} 


	and a.COMPETITIVE_FLAG = #{COMPETITIVE_FLAG} 


	and a.RECOMMEND_FLAG = #{RECOMMEND_FLAG} 


	and a.DATA_TYPE = #{DATA_TYPE} 

ORDER BY A.POWER,A.ADD_TIME DESC
